Ext.define('CCIVIC.view.Foto', {
    extend: 'Ext.Panel',

    config: {
        ui: '',
        layout: {
            type: 'vbox'
        },
        items: [
            {
                xtype: 'container',
                itemId: 'FotoContainer',
                items: [
                    {
                        xtype: 'image',
                        centered: true,
                        height: 201,
                        itemId: 'imgFoto',
                        width: 201
                    }
                ]
            },
            {
                xtype: 'toolbar',
                docked: 'bottom',
                itemId: 'fotoBar',
                items: [
                    {
                        xtype: 'button',
                        itemId: 'btnOk',
                        ui: 'round',
                        iconMask: true,
                        text: 'Acceptar'
                    },
                    {
                        xtype: 'button',
                        itemId: 'btnCapturar',
                        ui: 'round',
                        text: 'Capturar'
                    },
                    {
                        xtype: 'button',
                        itemId: 'btnSeleccionar',
                        ui: 'round',
                        text: 'Seleccionar'
                    }
                ]
            }
        ],
        listeners: [
            {
                fn: 'onBtnOkTap',
                event: 'tap',
                delegate: '#btnOk'
            },
            {
                fn: 'onBtnCapturarTap',
                event: 'tap',
                delegate: '#btnCapturar'
            },
            {
                fn: 'onBtnSeleccionarTap',
                event: 'tap',
                delegate: '#btnSeleccionar'
            }
        ]
    },

    onBtnOkTap: function(button, e, options) {
        this.getParent().pop();
    },

    onBtnCapturarTap: function(button, e, options) {
        Ext.device.Camera.capture({
            success: function(uri){
                // Ext.getStore('Image').add({src: uri});
                console.log(uri);           
                Ext.ComponentQuery.query('#imgFoto')[0].setSrc(uri);

            },
            scope: this,
            source: 'camera',
            destination: 'file'
        });
    },

    onBtnSeleccionarTap: function(button, e, options) {
        Ext.device.Camera.capture({
            success: function(uri){
                //Ext.getStore('Image').add({src: uri});         
                Ext.ComponentQuery.query('#imgFoto')[0].setSrc(uri);        
            },
            scope: this,
            source: 'library',
            destination: 'file'
        });
    }

});